import React from 'react';

const cssstyle = {
  padding: '20px 20px',
  backgroundColor: '#ffe9c5',
  color: '#0000FF',
  borderRadius: '40px',
  textAlign: 'right',
  marginTop: '0.67em',
  marginBottom: '0.67em',
  marginLeft: '0',
  marginRight: '0',
  fontWeight: 'bold'
};

function Cards(props) {
  return (
    <>
      <div className="cards">
        <div className="card">
          <img src={props.imgsrc} alt="cardImage" className="card_img" />
          <div className="card_info">
            <span className="card_category" style={cssstyle}>{props.category}</span>
            <h3 className="card_title">DARK</h3>
            <a href="https://www.netflix.com/in/title/80100172" target="_blank">
              <button className="button">Watch Dark</button>
            </a>
          </div>
        </div>
      </div>

    </>
  )
};

export default Cards;
